import { ButtonIcon, ButtonGroup, AddIcon, InfoIcon, ButtonSpinner, ArrowUpIcon, Heading, Text, HStack, VStack, ThreeDotsIcon } from '@gluestack-ui/themed';
import React, { useState } from "react";
import { FormControl, FormControlLabel, FormControlLabelText, FormControlHelper, FormControlHelperText, FormControlError, FormControlErrorIcon, FormControlErrorText, Input, InputField, Button, ButtonText, AlertCircleIcon, CheckboxGroup, Checkbox, Box, Radio, RadioGroup, RadioIndicator, RadioIcon, RadioLabel, CircleIcon } from '@gluestack-ui/themed';





const Validar = () => {

    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });

    const isFormEmailValid = formData.email.includes("@") && formData.email.includes(".com");
    const isFormPassValid = formData.password.length >= 6;

    const handleInputChange = (field: string, value: string) => {
        setFormData({
            ...formData,
            [field]: value
        });
    };

    const handleSubmit = () => {
        if (isFormEmailValid) {
            console.log("Form submitted", formData);
        } else {
            console.log("Form is invalid");
        }
        if (isFormPassValid) {

            console.log("Form submitted", formData);

        } else {

            console.log("Form is invalid");
        }
    };

    return (<Box borderColor='darkBlue200' borderWidth={'$1'} p={'$1'} borderRadius={'$lg'} backgroundColor='white' alignItems='center' >

        <Text fontSize={"$2xl"} color='black' p={'$4'} >Bienvenidos</Text>
        
        <FormControl isInvalid={!isFormEmailValid} size={"lg"} isDisabled={false} isRequired={true}>
            <FormControlLabel>
                <FormControlLabelText>Email</FormControlLabelText>
            </FormControlLabel>
            <Input>
                <InputField type="text" placeholder="Email" onChangeText={value => handleInputChange("email", value)} />
            </Input>
            <FormControlError>
                <FormControlErrorIcon as={AlertCircleIcon} />
                <FormControlErrorText>
                    El Email debe de contener "@" y ".com"
                </FormControlErrorText>
            </FormControlError>
        </FormControl>

        <FormControl isInvalid={!isFormPassValid} size={"lg"} isDisabled={false} isRequired={true}>

            <FormControlLabel >
                <FormControlLabelText>Password</FormControlLabelText>
            </FormControlLabel>
            <Input>
                <InputField type="password" placeholder="Password" onChangeText={value => handleInputChange("password", value)} />

            </Input>
            <FormControlHelper>
                <FormControlHelperText>
                    Debes de ingresar por lo menos 6 caracteres.
                </FormControlHelperText>
            </FormControlHelper>


            <Box>

                <Button action={"primary"} variant={"solid"} size={"lg"} marginTop={20} isDisabled={!isFormEmailValid && !isFormPassValid} onPress={handleSubmit}>
                    <ButtonText>Iniciar</ButtonText>
                </Button>
            </Box>
        </FormControl>

    </Box>);

};


export default Validar
