import { config } from '@gluestack-ui/config';
import { NavigationContainer } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { DrawerContent, DrawerItem, createDrawerNavigator } from '@react-navigation/drawer';
import categorias from './components/Inicio';
import configuracion_usuario from './components/configuracion_usuario';
import { Button, Headline } from 'react-native-paper';
import modo from './components/modo_oscuro';
import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, StatusBar } from 'react-native';
import { Switch } from 'react-native-gesture-handler/lib/typescript/components/GestureComponents';

const App = () => {
  const [darkMode, setDarkMode] = useState<boolean>(false);

  const toggleDarkMode = () => {
    setDarkMode(prevMode => !prevMode);
  };

  return (
    <View style={[styles.container, { backgroundColor: darkMode ? '#333' : '#fff' }]}>
      <StatusBar barStyle={darkMode ? 'light-content' : 'dark-content'} />
      <View style={styles.header}>
        <Text style={[styles.headerText, { color: darkMode ? '#fff' : '#333' }]}>
          {darkMode ? 'Modo Oscuro' : 'Modo Claro'}
        </Text>
        <Switch
          trackColor={{ false: '#007bff', true: '#007bff' }}
          thumbColor={darkMode ? '#f4f3f4' : '#f4f3f4'}
          ios_backgroundColor="#007bff"
          onValueChange={toggleDarkMode}
          value={darkMode}
        />
      </View>
      <View style={styles.main}>
        <Text style={[styles.contentText, { color: darkMode ? '#fff' : '#333' }]}>
          Contenido de la aplicaci√≥n.
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 50,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
  },
  headerText: {
    fontSize: 24,
  },
  main: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  contentText: {
    fontSize: 20,
  },
});

export default App;


