import { Center, Icon, Link, LinkText } from '@gluestack-ui/themed';
import { ButtonIcon, ButtonGroup, AddIcon, InfoIcon, ButtonSpinner, ArrowUpIcon, Heading, Text, HStack, VStack, ThreeDotsIcon } from '@gluestack-ui/themed';
import React, { useState } from "react";
import { FormControl, FormControlLabel, FormControlLabelText, FormControlHelper, FormControlHelperText, FormControlError, FormControlErrorIcon, FormControlErrorText, Input, InputField, Button, ButtonText, AlertCircleIcon, CheckboxGroup, Checkbox, Box, Radio, RadioGroup, RadioIndicator, RadioIcon, RadioLabel, CircleIcon } from '@gluestack-ui/themed';
import { useNavigation } from '@react-navigation/native';


function Crear_Usuario(){
    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });
    const isFormEmailValid = formData.email.includes("@") && formData.email.includes(".com");
    const isFormPassValid = formData.password.length >= 6;
    const handleInputChange = (field: string, value: string) => {
        setFormData({
            ...formData,
            [field]: value
        });
    };
    const handleSubmit = () => {
        if (isFormPassValid) {
            console.log("Form submitted", formData);
        } else {
            console.log("Form is invalid");
        }
    };
    return <Box borderColor='darkBlue200' borderWidth={'$1'} p={'$1'} borderRadius={'$lg'} backgroundColor='white' alignItems='center'>

        <Text fontSize={"$2xl"} color='black' p={'$4'}>Bienvenidos Registre su Usuario por favor</Text>

        <FormControl size={"lg"} isDisabled={false} isRequired={true}>
            <FormControlLabel>
                <FormControlLabelText>Usuario</FormControlLabelText>
            </FormControlLabel>
            <Input>
                <InputField type="text" placeholder="Usuario" onChangeText={value => handleInputChange("email", value)} />
            </Input>
            <FormControlError>
                <FormControlErrorIcon />
                <FormControlErrorText>
                    Agrega tu usuario
                </FormControlErrorText>
            </FormControlError>


            <FormControl isInvalid={!isFormPassValid} size={"lg"} isDisabled={false} isRequired={true}>

                <FormControlLabel>
                    <FormControlLabelText>Contraseña</FormControlLabelText>
                </FormControlLabel>
                <Input>
                    <InputField type="password" placeholder="Contraseña" onChangeText={value => handleInputChange("password", value)} />

                </Input>
                <FormControlHelper>
                    <FormControlHelperText>
                        Debes de ingresar por lo menos 6 caracteres.
                    </FormControlHelperText>
                </FormControlHelper>


                <Box>

                    <Button action={"primary"} variant={"solid"} bgColor='#002851' size={"lg"} marginTop={20} isDisabled={!isFormPassValid} onPress={handleSubmit}>
                        <ButtonText>Iniciar</ButtonText>
                    </Button>
            
        <Text marginTop={"$4.5"} >Si no cuenta con un usuario y contraseña haga click en el siguiente link </Text>

        <Link marginTop={"$2.5"} alignSelf='center'>
        <LinkText>Crear Usuario</LinkText>
      </Link>
      


                </Box>
            </FormControl>
        </FormControl>

    </Box>;



}

export default Crear_Usuario;
