// configuracion_usuario.js
import React from 'react';
import { View, Text, Switch, StyleSheet } from 'react-native';
import { useTheme } from './TextContext'; // Importa el contexto

const ConfiguracionUsuario = () => {
  const { theme, toggleTheme } = useTheme(); // Usa el contexto

  const lightTheme = {
    backgroundColor: '#ffffff',
    textColor: '#000000',
  };
  
  const darkTheme = {
    backgroundColor: '#000000',
    textColor: '#ffffff',
  };

  return (
    <View style={[styles.container, { backgroundColor: theme.backgroundColor }]}>
      <Text style={[styles.text, { color: theme.textColor }]}>Â¡Estoy en modo {theme === darkTheme ? 'oscuro' : 'claro'}!</Text>
      <Switch 
        trackColor={{ false: "#767577", true: "#81b0ff" }}
        thumbColor={theme === darkTheme ? "#f5dd4b" : "#f4f3f4"}
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleTheme}
        value={theme === darkTheme}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  text: {
    fontSize: 20,
    marginBottom: 20,
  },
});

export default ConfiguracionUsuario;
