import React from 'react';
import { GluestackUIProvider, Box, VStack, View } from '@gluestack-ui/themed';
import { config } from '@gluestack-ui/config';
import { NavigationContainer } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { DrawerContent, DrawerItem, createDrawerNavigator } from '@react-navigation/drawer';
import categorias from './components/Inicio';
import configuracion_usuario from './components/configuracion_usuario';
import { Button, Headline } from 'react-native-paper';
import modo from './components/modo_oscuro';

const App = () => {
  const [darkMode, setDarkMode] = useState<boolean>(false);

  const toggleDarkMode = () => {
    setDarkMode(prevMode => !prevMode);
  };

  return (
    <View style={[styles.container, { backgroundColor: darkMode ? '#333' : '#fff' }]}>
      <StatusBar barStyle={darkMode ? 'light-content' : 'dark-content'} />
      <View style={styles.header}>
        <Text style={[styles.headerText, { color: darkMode ? '#fff' : '#333' }]}>
          {darkMode ? 'Modo Oscuro' : 'Modo Claro'}
        </Text>
        <TouchableOpacity onPress={toggleDarkMode}>
          <Text style={styles.buttonText}>
            {darkMode ? 'Cambiar a Modo Claro' : 'Cambiar a Modo Oscuro'}
          </Text>
        </TouchableOpacity>
      </View>
      <View style={styles.main}>
        <Text style={[styles.contentText, { color: darkMode ? '#fff' : '#333' }]}>
          Contenido de la aplicaci√≥n.
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 50,
  },
  header: {
    padding: 20,
    alignItems: 'center',
  },
  headerText: {
    fontSize: 24,
  },
  buttonText: {
    fontSize: 18,
    color: '#007bff',
    marginTop: 10,
  },
  main: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  contentText: {
    fontSize: 20,
  },
});

export default App;

