import { config } from '@gluestack-ui/config';
import { DarkTheme, NavigationContainer } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { DrawerContent, DrawerItem, createDrawerNavigator } from '@react-navigation/drawer';
import categorias from './components/Inicio';
import configuracion_usuario from './components/configuracion_usuario';
import { Button, DefaultTheme, Headline, PaperProvider, ThemeProvider } from 'react-native-paper';
import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, StatusBar } from 'react-native';
import { GluestackUIProvider } from '@gluestack-ui/themed';


const Drawer = createDrawerNavigator();

export default function App() {
  const [isDarkTheme, setIsDarkTheme] = React.useState(false);

  // Define el tema de navegaci√≥n
  const navigationTheme = isDarkTheme ? navigationTheme : navigationTheme;

  // Define el tema de PaperProvider
  const paperTheme = isDarkTheme ? DarkTheme : DefaultTheme;
  return(
    <PaperProvider theme={paperTheme}>
  <NavigationContainer>
      <GluestackUIProvider config={config}>

        <Drawer.Navigator initialRouteName="Home">

          <Drawer.Screen name="Inicio" component={categorias} />
          <Drawer.Screen name="Configuracion" component={configuracion_usuario} />

        </Drawer.Navigator>



      </GluestackUIProvider>
    </NavigationContainer>
    </PaperProvider>

    );


};
